
public class Sum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sumOdds = 0;
		for ( int i = 1; i <= 1000; i++)
		{ 
			if (i % 2 != 0)
				sumOdds = sumOdds + i;
				
				
		}
			System.out.println("The sum of all odd numbers from 1-1000 ="  + sumOdds);
	
		
		
		
		
	}

}
